#!/bin/bash

psql -d $DB_NAME -U $DB_USER -h $DB_HOST -p $DB_PORT -c "CREATE TABLE IF NOT EXISTS $DB_TABLE ("admin_user_id" INT GENERATED ALWAYS AS IDENTITY (START WITH 1 INCREMENT BY 1) PRIMARY KEY,"uuid" UUID NOT NULL DEFAULT gen_random_uuid (),"status" boolean NOT NULL DEFAULT TRUE,"email" varchar NOT NULL,"password" varchar NOT NULL,"full_name" varchar DEFAULT NULL,"created_at" TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,"updated_at" TIMESTAMP WITH TIME ZONE DEFAULT CURRENT_TIMESTAMP,CONSTRAINT "ADMIN_USER_EMAIL_UNIQUE" UNIQUE ("email"),CONSTRAINT "ADMIN_USER_UUID_UNIQUE" UNIQUE ("uuid"));"
cd ..